

const quantity = document.querySelectorAll('.quantity__button').forEach(
    (btn) => {
        btn.addEventListener('click', function() {
            const direction = this.dataset.direction
            const inp = this.parentElement
            .querySelector('.quantity')
            const currentValue = +inp.value
            let newValue
            
            if (direction === 'plus') {
                newValue = currentValue + 1 < 99 ? currentValue + 1 : 99
            } else {
                newValue = currentValue - 1 > 0 ? currentValue - 1 : 0
            }
            
            inp.value = newValue
        })
    }
)





const disableScroll = function() {
    let paddingOffset = window.innerWidth - document.body.offsetWidth + 'px'
    document.body.classList.toggle('scroll-lock')
    document.body.style.paddingRight = paddingOffset
}

const sumbit = document.querySelector('.contacts__button').addEventListener('click', (e) => {
    e.preventDefault()
    const form = document.querySelector('.contacts__form')
    const popup = document.querySelector('.success')
    const exit = document.querySelector('.exit')

    popup.classList.add('success-active')

    exit.addEventListener('click', () => {
        popup.classList.remove('success-active')
    })

})

$(document).ready(function(){
    $('.feedback__list').slick({
        dots: true,
        slidesToScroll: 2,
        slidesToShow: 2,
        responsive: [
            {
                breakpoint: 1124,
                settings: {
                    slidesToScroll: 1,
                    slidesToShow: 1
                }
            }
        ]
    });
});










